export function ParseStringToXml(text) {
    try {
        var xml = null;

        if (window.DOMParser) {

            var parser = new DOMParser();
            xml = parser.parseFromString(text, 'text/xml');

            var found = xml.getElementsByTagName('parsererror');

            if (!found || !found.length || !found[0].childNodes.length) {
